import React, { useState } from "react";
import axios from "axios";
import "./App.css";

function App() {
  const [city, setCity] = useState(""); // to hold the city input
  const [weather, setWeather] = useState(null); // to hold the fetched weather data
  const [error, setError] = useState(null); // to handle errors

  const apiKey = "c76284f01da16293c03a723ffbab23de"; // Replace with your OpenWeather API key

  const handleCityChange = (event) => {
    setCity(event.target.value);
  };

  const fetchWeather = async () => {
    if (city) {
      try {
        // Using OpenWeather API to get the weather based on city name
        const response = await axios.get(
          `https://api.openweathermap.org/data/2.5/weather?q=${city},za&appid=${apiKey}&units=metric`
        );
        setWeather(response.data);
        setError(null); // Reset error if the fetch is successful
      } catch (error) {
        setWeather(null); // Reset weather data if the fetch fails
        setError("City not found. Please check the spelling or try another city in South Africa.");
      }
    }
  };

  return (
    <div className="App">
      <h1>South Africa Weather App</h1>
      <input
        type="text"
        placeholder="Enter city (e.g., Cape Town)"
        value={city}
        onChange={handleCityChange}
      />
      <button onClick={fetchWeather}>Get Weather</button>

      {error && <p style={{ color: "red" }}>{error}</p>}

      {weather && (
        <div className="weather-details">
          <h2>{weather.name}, {weather.sys.country}</h2>
          <p>Temperature: {weather.main.temp}Â°C</p>
          <p>Weather: {weather.weather[0].description}</p>
          <p>Humidity: {weather.main.humidity}%</p>
          <p>Wind Speed: {weather.wind.speed} m/s</p>
        </div>
      )}
    </div>
  );
}

export default App;
